mapper.select:
select M.ID AS "id", M.VERSION AS "version", M.PARENT_ID AS "parentId", M.NAME as "name", M.IS_LEAF AS "isLeaf", M.LEVEL AS "level", M.TARGET_NAME AS "targetName" FROM PMS_MENU M
And M.PARENT_ID = #{parentId}
ORDER BY M.NUMBER ASC

select * from PMS_MENU

	 and IS_LEAF = #{isLeaf}
	 and NAME = #{name}
	 and PARENT_ID = #{parentId}
